    private void BigualActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BigualActionPerformed
        switch (operador) {
            case "+":
                Suma sum = new Suma(valor1, valor2);
                total = sum.Sumar();
                break;
            case "-":
                Resta rest = new Resta(valor1, valor2);
                total = rest.Restar();
                break;
            case "x":
                Multiplicacion mult = new Multiplicacion(valor1, valor2);
                total = mult.Multiplicar();
                break;
            case "รท":
                Division divi = new Division(valor1, valor2);
                total = divi.Dividir();
                break;
        }
        Ltotales.setText(String.valueOf(valor1) + " " + operador + " " + String.valueOf(valor2) + " = ");
        Lresultado.setText(String.valueOf(total));
        valor1 = total;
    }//GEN-LAST:event_BigualActionPerformed

    private void BborrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BborrarActionPerformed
        Lresultado.setText("");
        Ltotales.setText("");
        valor1 = 0;
        valor2 = 0;
        total = 0;
        CambioEstado = false;
    }//GEN-LAST:event_BborrarActionPerformed

    private void BpuntoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BpuntoActionPerformed
        contenido = Lresultado.getText();
        if (contenido.length() <= 0) {
            Lresultado.setText("0.");
        } else if (Lresultado.getText().contains(".")) {
        } else {
            Lresultado.setText(Lresultado.getText() + ".");
            punto = false;
        }
    }//GEN-LAST:event_BpuntoActionPerformed
    private void AlmacenarValores() {
        if (CambioEstado == false) {
            valor1 = Float.parseFloat(Lresultado.getText());
        } else {
            valor2 = Float.parseFloat(Lresultado.getText());
        }
    }

